# Решите численно уравнение x + log(x) = a  # Функция принимает на вход afunction f = task2(a)  x = 1  if (a > 0)    while (abs((x + log(x) - a) / (1 + 1/x)) > 0.00001)      x = x - (x + log(x) - a) / (1 + 1/x)     endwhile    f = x  else    while (abs((1/x - log(x) - a) / (-1/(x*x) - 1/x)) > 0.0001)      x = x - (1/x - log(x) - a) / (-1/(x*x) - 1/x)     endwhile    f = 1/x  endifendfunction