function task5(lp, xrange, yrange)  a = lp(1)  b = lp(2)   c = lp(3)  points = [    -(c + b * yrange(1)) / a, yrange(1)     -(c + b * yrange(2)) / a, yrange(2)     xrange(1), -(c + a * xrange(1)) / b    xrange(2), -(c + a * xrange(2)) / b]      idx = (points(:, 1) >= xrange(1) & points(:, 1) <= xrange(2) &        points(:, 2) >= yrange(1) & points(:, 2) <= yrange(2))         points = points(idx, :)  plot(points(:,1), points(:,2))endfunction